const modulesWithoutProtocol = builtinModules.filter(
  (mod) => !mod.startsWith('node:'),
)

/**
 * The `node:` protocol was added in Node.js v14.18.0.
 * @see https://nodejs.org/api/esm.html#node-imports
 */
export function NodeProtocolPlugin(nodeProtocolOption: 'strip' | true): Plugin {
  if (nodeProtocolOption === 'strip') {
    const regex = new RegExp(`^node:(${modulesWithoutProtocol.join('|')})$`)

    return {
      name: 'tsdown:node-protocol:strip',
      resolveId: {
        order: 'pre',
        filter: { id: regex },
        handler(id) {
          return {
            id: id.slice(5), // strip the `node:` prefix
            external: true,
            moduleSideEffects: false,
          }
        },
      },
    }
  }

  // create regex from builtin modules
  // filter without `node:` prefix
  const builtinModulesRegex = new RegExp(
    `^(${modulesWithoutProtocol.join('|')})$`,
  )

  return {
    name: 'tsdown:node-protocol:add',
    resolveId: {
      order: 'pre',
      filter: { id: builtinModulesRegex },
      handler(id) {
        return {
          id: `node:${id}`,
          external: true,
          moduleSideEffects: false,
        }
      },
    },
  }
}

import { chmod } from 'node:fs/promises'
import path from 'node:path'
import { underline } from 'ansis'
import { fsExists } from '../utils/fs.ts'
import { prettyFormat, prettyName, type Logger } from '../utils/logger.ts'
import type { Plugin } from 'rolldown'

const RE_SHEBANG = /^#!.*/

export function ShebangPlugin(
  logger: Logger,
  cwd: string,
  name?: string,
  isMultiFormat?: boolean,
): Plugin {
  return {
    name: 'tsdown:shebang',
    async writeBundle(options, bundle) {
      for (const chunk of Object.values(bundle)) {
        if (chunk.type !== 'chunk' || !chunk.isEntry) continue
        if (!RE_SHEBANG.test(chunk.code)) continue

        const filepath = path.resolve(
          cwd,
          options.file || path.join(options.dir!, chunk.fileName),
        )
        if (await fsExists(filepath)) {
          logger.info(
            prettyName(name),
            isMultiFormat && prettyFormat(options.format),
            `Granting execute permission to ${underline(path.relative(cwd, filepath))}`,
          )
          await chmod(filepath, 0o755)
        }
      }
    },
  }
}

