import { builtinModules } from 'node:module'
import type { Plugin } from 'rolldown'

const modulesWithoutProtocol = builtinModules.filter(
  (mod) => !mod.startsWith('node:'),
)

/**
 * The `node:` protocol was added in Node.js v14.18.0.
 * @see https://nodejs.org/api/esm.html#node-imports
 */
export function NodeProtocolPlugin(nodeProtocolOption: 'strip' | true): Plugin {
  if (nodeProtocolOption === 'strip') {
    const regex = new RegExp(`^node:(${modulesWithoutProtocol.join('|')})$`)

    return {
      name: 'tsdown:node-protocol:strip',
      resolveId: {
        order: 'pre',
        filter: { id: regex },
        handler(id) {
          return {
            id: id.slice(5), // strip the `node:` prefix
            external: true,
            moduleSideEffects: false,
          }
        },
      },
    }
  }

  // create regex from builtin modules
  // filter without `node:` prefix
  const builtinModulesRegex = new RegExp(
    `^(${modulesWithoutProtocol.join('|')})$`,
  )

  return {
    name: 'tsdown:node-protocol:add',
    resolveId: {
      order: 'pre',
      filter: { id: builtinModulesRegex },
      handler(id) {
        return {
          id: `node:${id}`,
          external: true,
          moduleSideEffects: false,
        }
      },
    },
  }
}

import { bgRed, bgYellow, blue, green, rgb, yellow, type Ansis } from 'ansis'
import { noop } from './general.ts'
import type { InternalModuleFormat } from 'rolldown'

export type LogType = 'error' | 'warn' | 'info'
export type LogLevel = LogType | 'silent'

export interface LoggerOptions {
  customLogger?: Logger
  console?: Console
  failOnWarn?: boolean
}

export const LogLevels: Record<LogLevel, number> = {
  silent: 0,
  error: 1,
  warn: 2,
  info: 3,
}

export interface Logger {
  level: LogLevel
  info: (...args: any[]) => void
  warn: (...args: any[]) => void
  warnOnce: (...args: any[]) => void
  error: (...args: any[]) => void
  success: (...args: any[]) => void
}

function format(msgs: any[]) {
  return msgs.filter((arg) => arg !== undefined && arg !== false).join(' ')
}

const warnedMessages = new Set<string>()

export function createLogger(
  level: LogLevel = 'info',
  {
    customLogger,
    console = globalThis.console,
    failOnWarn = false,
  }: LoggerOptions = {},
): Logger {
  if (customLogger) {
    return customLogger
  }

  function output(type: LogType, msg: string) {
    const thresh = LogLevels[logger.level]
    if (thresh < LogLevels[type]) return

    const method = type === 'info' ? 'log' : type
    console[method](msg)
  }

  const logger: Logger = {
    level,

    info(...msgs: any[]): void {
      output('info', `${blue``} ${format(msgs)}`)
    },

    warn(...msgs: any[]): void {
      const message = format(msgs)
      if (failOnWarn) {
        throw new Error(message)
      }
      warnedMessages.add(message)
      output('warn', `\n${bgYellow` WARN `} ${message}\n`)
    },

    warnOnce(...msgs: any[]): void {
      const message = format(msgs)
      if (warnedMessages.has(message)) {
        return
      }

      if (failOnWarn) {
        throw new Error(message)
      }
      warnedMessages.add(message)

      output('warn', `\n${bgYellow` WARN `} ${message}\n`)
    },

    error(...msgs: any[]): void {
      output('error', `\n${bgRed` ERROR `} ${format(msgs)}\n`)
    },

    success(...msgs: any[]): void {
      output('info', `${green``} ${format(msgs)}`)
    },
  }
  return logger
}

export const globalLogger: Logger = createLogger()

export function prettyName(name?: string): string | undefined {
  if (!name) return undefined
  return generateColor(name)(`[${name}]`)
}

export function prettyFormat(format: InternalModuleFormat): string {
  const formatColor = format === 'es' ? blue : format === 'cjs' ? yellow : noop

  let formatText: string
  switch (format) {
    case 'es':
      formatText = 'ESM'
      break
    default:
      formatText = format.toUpperCase()
      break
  }

  return formatColor(`[${formatText}]`)
}

// Copied from https://github.com/antfu/vscode-pnpm-catalog-lens - MIT License
const colors = new Map<string, Ansis>()
export function generateColor(name: string = 'default'): Ansis {
  if (colors.has(name)) {
    return colors.get(name)!
  }
  let color: Ansis
  if (name === 'default') {
    color = blue
  } else {
    let hash = 0
    for (let i = 0; i < name.length; i++)
