  const normalized = { ...config, ...inlineConfig }
  const rootCwd = normalized.cwd || process.cwd()
  let { workspace } = normalized
  if (!workspace) return { configs: [normalized], files: [] }

  if (workspace === true) {
    workspace = {}
  } else if (typeof workspace === 'string' || Array.isArray(workspace)) {
    workspace = { include: workspace }
  }

  let {
    include: packages = 'auto',
    exclude = DEFAULT_EXCLUDE_WORKSPACE,
    config: workspaceConfig,
  } = workspace
  if (packages === 'auto') {
    packages = (
      await glob('**/package.json', {
        ignore: exclude,
        cwd: rootCwd,
        expandDirectories: false,
      })
    )
      .filter((file) => file !== 'package.json') // exclude root package.json
      .map((file) => slash(path.resolve(rootCwd, file, '..')))
  } else {
    packages = (
      await glob(packages, {
        ignore: exclude,
        cwd: rootCwd,
        onlyDirectories: true,
        absolute: true,
        expandDirectories: false,
      })
    ).map((file) => slash(path.resolve(file)))
  }

  if (packages.length === 0) {
    throw new Error('No workspace packages found, please check your config')
  }

  if (inlineConfig.filter) {
    inlineConfig.filter = resolveRegex(inlineConfig.filter)
    packages = packages.filter((path) => {
      return typeof inlineConfig.filter === 'string'
        ? path.includes(inlineConfig.filter)
        : Array.isArray(inlineConfig.filter)
          ? inlineConfig.filter.some((filter) => path.includes(filter))
          : inlineConfig.filter!.test(path)
    })
    if (packages.length === 0) {
      throw new Error('No packages matched the filters')
    }
  }

  const files: string[] = []
  const configs = (
    await Promise.all(
      packages.map(async (cwd) => {
        debug('loading workspace config %s', cwd)
        const { configs, file } = await loadConfigFile(
          {
            ...inlineConfig,
            config: workspaceConfig,
            cwd,
          },
          cwd,
        )
        if (file) {
          debug('loaded workspace config file %s', file)
          files.push(file)
        } else {
          debug('no workspace config file found in %s', cwd)
        }
        return configs.map(
          (config): UserConfig => ({
            ...normalized,
            cwd,
            ...config,
          }),
        )
      }),
    )
  ).flat()

  return { configs, files }
}

async function resolveUserConfig(
  userConfig: UserConfig,
): Promise<ResolvedConfig> {
  let {
    entry,
    format = ['es'],
    plugins = [],
    clean = true,
    silent = false,
    logLevel = silent ? 'silent' : 'info',
    failOnWarn = false,
    customLogger,
    treeshake = true,
    platform = 'node',
    outDir = 'dist',
    sourcemap = false,
    dts,
    unused = false,
    watch = false,
    ignoreWatch,
    shims = false,
    skipNodeModulesBundle = false,
    publint = false,
    attw = false,
    fromVite,
    alias,
    tsconfig,
    report = true,
    target,
    env = {},
    copy,
    publicDir,
    hash,
    cwd = process.cwd(),
    name,
    workspace,
    external,
    noExternal,
    exports = false,
    bundle,
    unbundle = typeof bundle === 'boolean' ? !bundle : false,
    removeNodeProtocol,
    nodeProtocol,
    cjsDefault = true,
    globImport = true,
    inlineOnly,
    fixedExtension = platform === 'node',
  } = userConfig

  const logger = createLogger(logLevel, { customLogger, failOnWarn })

  if (typeof bundle === 'boolean') {
    logger.warn('`bundle` option is deprecated. Use `unbundle` instead.')
  }

  if (removeNodeProtocol && nodeProtocol) {
    throw new TypeError(
      '`removeNodeProtocol` is deprecated. Please only use `nodeProtocol` instead.',
    )
  }

  // Resolve nodeProtocol option with backward compatibility for removeNodeProtocol
  nodeProtocol =
    nodeProtocol ??
    // `removeNodeProtocol: true` means stripping the `node:` protocol which equals to `nodeProtocol: 'strip'`
    // `removeNodeProtocol: false` means keeping the `node:` protocol which equals to `nodeProtocol: false` (ignore it)
    (removeNodeProtocol ? 'strip' : false)

  outDir = path.resolve(cwd, outDir)
  clean = resolveClean(clean, outDir, cwd)

  const pkg = await readPackageJson(cwd)
  if (workspace) {
    name ||= pkg?.name
  }
  entry = await resolveEntry(logger, entry, cwd, name)
  if (dts == null) {
    dts = !!(pkg?.types || pkg?.typings || hasExportsTypes(pkg))
  }
  target = resolveTarget(logger, target, pkg, name)
  tsconfig = await resolveTsconfig(logger, tsconfig, cwd, name)
  if (typeof external === 'string') {
    external = resolveRegex(external)
  }
  if (typeof noExternal === 'string') {
    noExternal = resolveRegex(noExternal)
  }

  if (publint === true) publint = {}
  if (attw === true) attw = {}
  if (exports === true) exports = {}

  if (publicDir) {
    if (copy) {
      throw new TypeError(
        '`publicDir` is deprecated. Cannot be used with `copy`',
      )
    } else {
      logger.warn(
        `${blue`publicDir`} is deprecated. Use ${blue`copy`} instead.`,
      )
    }
  }

  if (fromVite) {
    const viteUserConfig = await loadViteConfig(
      fromVite === true ? 'vite' : fromVite,
      cwd,
    )
    if (viteUserConfig) {
      const viteAlias = viteUserConfig.resolve?.alias

      if ((Array.isArray as (arg: any) => arg is readonly any[])(viteAlias)) {
        throw new TypeError(
          'Unsupported resolve.alias in Vite config. Use object instead of array',
        )
      }
      if (viteAlias) {
        alias = { ...alias, ...viteAlias }
      }

      if (viteUserConfig.plugins) {
        plugins = [viteUserConfig.plugins as any, plugins]
      }
    }
  }

  ignoreWatch = toArray(ignoreWatch).map((ignore) => {
    ignore = resolveRegex(ignore)
    if (typeof ignore === 'string') {
      return path.resolve(cwd, ignore)
    }
    return ignore
  })

  if (noExternal != null && typeof noExternal !== 'function') {
    const noExternalPatterns = toArray(noExternal)
    noExternal = (id) => matchPattern(id, noExternalPatterns)
  }
  if (inlineOnly != null) {
    inlineOnly = toArray(inlineOnly)
  }

  const config: ResolvedConfig = {
    ...userConfig,
    entry,
    plugins,
    format: normalizeFormat(format),
    target,
    outDir,
    clean,
    logger,
    treeshake,
    platform,
    sourcemap,
    dts: dts === true ? {} : dts,
    report: report === true ? {} : report,
    unused,
    watch,
    ignoreWatch,
    shims,
    skipNodeModulesBundle,
    publint,
    attw,
    alias,
    tsconfig,
    cwd,
    env,
    pkg,
    copy: publicDir || copy,
    hash: hash ?? true,
    name,
    external,
    noExternal,
    exports,
    unbundle,
    nodeProtocol,
    cjsDefault,
    globImport,
    inlineOnly,
    fixedExtension,
  }

  return config
}

export async function mergeUserOptions<T extends object, A extends unknown[]>(
  defaults: T,
  user:
    | T
    | undefined
    | null
    | ((options: T, ...args: A) => Awaitable<T | void | null>),
  args: A,
): Promise<T> {
  const userOutputOptions =
    typeof user === 'function' ? await user(defaults, ...args) : user
  return { ...defaults, ...userOutputOptions }
}

import { Buffer } from 'node:buffer'
import path from 'node:path'
import { promisify } from 'node:util'
import { brotliCompress, gzip } from 'node:zlib'
import { bold, dim, green } from 'ansis'
import Debug from 'debug'
import { RE_DTS } from 'rolldown-plugin-dts/filename'
import { formatBytes } from '../utils/format.ts'
import { noop } from '../utils/general.ts'
import { prettyFormat, prettyName, type Logger } from '../utils/logger.ts'
import type { OutputAsset, OutputChunk, Plugin } from 'rolldown'

const debug = Debug('tsdown:report')
const brotliCompressAsync = promisify(brotliCompress)
const gzipAsync = promisify(gzip)

interface SizeInfo {
  filename: string
  dts: boolean
  isEntry: boolean
  raw: number
  gzip: number
  brotli: number
  rawText: string
  gzipText?: string
  brotliText?: string
}

export interface ReportOptions {
  /**
   * Enable/disable gzip-compressed size reporting.
   * Compressing large output files can be slow, so disabling this may increase build performance for large projects.
   *
   * @default true
   */
  gzip?: boolean

  /**
   * Enable/disable brotli-compressed size reporting.
   * Compressing large output files can be slow, so disabling this may increase build performance for large projects.
   *
   * @default false
   */
  brotli?: boolean

  /**
   * Skip reporting compressed size for files larger than this size.
   * @default 1_000_000 // 1MB
   */
  maxCompressSize?: number
}

const defaultOptions = {
  gzip: true,
  brotli: false,
  maxCompressSize: 1_000_000,
}

export function ReportPlugin(
  userOptions: ReportOptions,
  logger: Logger,
  cwd: string,
  cjsDts?: boolean,
  name?: string,
  isMultiFormat?: boolean,
): Plugin {
  const options = {
    ...defaultOptions,
    ...userOptions,
  }

  return {
    name: 'tsdown:report',
    async writeBundle(outputOptions, bundle) {
      const outDir = path.relative(
        cwd,
        outputOptions.file
          ? path.resolve(cwd, outputOptions.file, '..')
          : path.resolve(cwd, outputOptions.dir!),
      )

      const sizes: SizeInfo[] = []
      for (const chunk of Object.values(bundle)) {
        const size = await calcSize(options, chunk)
        sizes.push(size)
      }

      const filenameLength = Math.max(
        ...sizes.map((size) => size.filename.length),
      )

      // padding rawText, gzipText, brotliText to the same length
      const rawTextLength = Math.max(
        ...sizes.map((size) => size.rawText.length),
      )
      const gzipTextLength = Math.max(
        ...sizes.map((size) =>
          size.gzipText == null ? 0 : size.gzipText.length,
        ),
      )
      const brotliTextLength = Math.max(
        ...sizes.map((size) =>
          size.brotliText == null ? 0 : size.brotliText.length,
        ),
      )

      let totalRaw = 0
      for (const size of sizes) {
        size.rawText = size.rawText.padStart(rawTextLength)
        size.gzipText = size.gzipText?.padStart(gzipTextLength)
        size.brotliText = size.brotliText?.padStart(brotliTextLength)
        totalRaw += size.raw
      }

      // sort
      sizes.sort((a, b) => {
        // dts last
        if (a.dts !== b.dts) return a.dts ? 1 : -1
        // entry first
        if (a.isEntry !== b.isEntry) return a.isEntry ? -1 : 1
        // otherwise, sort by raw size descending
        return b.raw - a.raw
      })

      const nameLabel = prettyName(name)
      const formatLabel =
        isMultiFormat && prettyFormat(cjsDts ? 'cjs' : outputOptions.format)

      for (const size of sizes) {
        const filenameColor = size.dts ? green : noop

        logger.info(
          nameLabel,
          formatLabel,
          dim(outDir + path.sep) +
            filenameColor((size.isEntry ? bold : noop)(size.filename)),
          ` `.repeat(filenameLength - size.filename.length),
          dim(size.rawText),
          options.gzip && size.gzipText && dim` gzip: ${size.gzipText}`,
          options.brotli &&
            size.brotliText &&
            dim` brotli: ${size.brotliText}`,
        )
      }

      const totalSizeText = formatBytes(totalRaw)
      logger.info(
        nameLabel,
        formatLabel,
        `${sizes.length} files, total: ${totalSizeText}`,
      )
    },
  }
}

async function calcSize(
  options: Required<ReportOptions>,
  chunk: OutputAsset | OutputChunk,
): Promise<SizeInfo> {
  debug(`Calculating size for`, chunk.fileName)

  const content = chunk.type === 'chunk' ? chunk.code : chunk.source

  const raw = Buffer.byteLength(content, 'utf8')
  debug('[size]', chunk.fileName, raw)

  let gzip: number = Infinity
  let brotli: number = Infinity
  if (raw > options.maxCompressSize) {
    debug(chunk.fileName, 'file size exceeds limit, skip gzip/brotli')
  } else {
    if (options.gzip) {
      gzip = (await gzipAsync(content)).length
      debug('[gzip]', chunk.fileName, gzip)
    }
    if (options.brotli) {
      brotli = (await brotliCompressAsync(content)).length
      debug('[brotli]', chunk.fileName, brotli)
    }
  }

  return {
    filename: chunk.fileName,
    dts: RE_DTS.test(chunk.fileName),
    isEntry: chunk.type === 'chunk' && chunk.isEntry,
    raw,
    rawText: formatBytes(raw)!,
    gzip,
    gzipText: formatBytes(gzip),
    brotli,
    brotliText: formatBytes(brotli),
  }
}

import path from 'node:path'
import { fsCopy } from '../utils/fs.ts'
import { toArray } from '../utils/general.ts'
import type { ResolvedConfig } from '../config/index.ts'
import type { Arrayable, Awaitable } from '../utils/types.ts'

export interface CopyEntry {
  from: string
  to: string
}
export type CopyOptions = Arrayable<string | CopyEntry>
export type CopyOptionsFn = (options: ResolvedConfig) => Awaitable<CopyOptions>

export async function copy(options: ResolvedConfig): Promise<void> {
  if (!options.copy) return

  const copy: CopyOptions =
    typeof options.copy === 'function'
      ? await options.copy(options)
      : options.copy

  await Promise.all(
    toArray(copy).map((dir) => {
      const from = typeof dir === 'string' ? dir : dir.from
      const to =
        typeof dir === 'string'
          ? path.resolve(options.outDir, path.basename(from))
          : dir.to
      return cp(options.cwd, from, to)
    }),
  )
}

function cp(cwd: string, from: string, to: string): Promise<void> {
  return fsCopy(path.resolve(cwd, from), path.resolve(cwd, to))
}

