  if (!options.exports) return

  const { outDir, pkg } = options
  if (!pkg) {
    throw new Error('`package.json` not found, cannot write exports')
  }

  const { publishExports, ...generated } = await generateExports(
    pkg,
    outDir,
    chunks,
    options.exports,
