      moduleTypes: loader,
      logLevel: logger.level === 'error' ? 'silent' : logger.level,
      onLog: cjsDefault
        ? (level, log, defaultHandler) => {
            // suppress mixed export warnings if cjsDefault is enabled
            if (log.code === 'MIXED_EXPORT') return
            defaultHandler(level, log)
          }
        : undefined,
    },
    config.inputOptions,
    [format, { cjsDts }],
  )

  return inputOptions
}

export async function resolveOutputOptions(
  inputOptions: InputOptions,
  config: ResolvedConfig,
  format: NormalizedFormat,
  cjsDts: boolean,
): Promise<OutputOptions> {
  const {
    entry,
    outDir,
    sourcemap,
    minify,
    unbundle,
    banner,
    footer,
    cjsDefault,
  } = config

  const [entryFileNames, chunkFileNames] = resolveChunkFilename(
    config,
    inputOptions,
    format,
  )
  const outputOptions: OutputOptions = await mergeUserOptions(
    {
      format: cjsDts ? 'es' : format,
      name: config.globalName,
      sourcemap,
      dir: outDir,
      exports: cjsDefault ? 'auto' : 'named',
      minify: !cjsDts && minify,
      entryFileNames,
      chunkFileNames,
      preserveModules: unbundle,
      preserveModulesRoot: unbundle
        ? lowestCommonAncestor(...Object.values(entry))
        : undefined,
      banner: resolveChunkAddon(banner, format),
      footer: resolveChunkAddon(footer, format),
    },
    config.outputOptions,
    [format, { cjsDts }],
  )
  return outputOptions
}

export async function getDebugRolldownDir(): Promise<string | undefined> {
  if (!debug.enabled) return
  return await mkdtemp(join(tmpdir(), 'tsdown-config-'))
}

export async function debugBuildOptions(
  dir: string,
  name: string | undefined,
  format: NormalizedFormat,
  buildOptions: BuildOptions,
): Promise<void> {
  const outFile = join(dir, `tsdown.config.${format}.js`)

  handlePluginInspect(buildOptions.plugins)
  const serialized = util.formatWithOptions(
    {
      depth: null,
      maxArrayLength: null,
      maxStringLength: null,
    },
    buildOptions,
  )
  const code = `/*
Auto-generated rolldown config for tsdown debug purposes
tsdown v${pkg.version}, rolldown v${rolldownVersion}
Generated on ${new Date().toISOString()}
Package name: ${name || 'not specified'}
*/

export default ${serialized}\n`
  await writeFile(outFile, code)
  debug(
    'Wrote debug rolldown config for "%s" (%s) -> %s',
    name || 'default name',
    format,
    outFile,
  )
}

function handlePluginInspect(plugins: RolldownPluginOption) {
  if (Array.isArray(plugins)) {
    for (const plugin of plugins) {
      handlePluginInspect(plugin)
    }
  } else if (
    typeof plugins === 'object' &&
    plugins !== null &&
    'name' in plugins
  ) {
    ;(plugins as any)[util.inspect.custom] = function (
      depth: number,
      options: InspectOptionsStylized,
      inspect: typeof util.inspect,
    ) {
      if ('_options' in plugins) {
        return inspect(
          { name: plugins.name, options: (plugins as any)._options },
          options,
        )
      } else {
        return `"rolldown plugin: ${plugins.name}"`
      }
    }
  }
}

import process from 'node:process'
import readline from 'node:readline'
import { bold, dim } from 'ansis'
import { globalLogger } from '../utils/logger.ts'

// Copied from https://github.com/vitejs/vite/blob/main/packages/vite/src/node/shortcuts.ts - MIT License

export interface Shortcut {
  key: string
  description: string
  action: () => void | Promise<void>
}

export function shortcuts(restart: () => void): () => void {
  let actionRunning = false
  async function onInput(input: string) {
    if (actionRunning) return
    const SHORTCUTS: Shortcut[] = [
      {
        key: 'r',
        description: 'reload config and rebuild',
        action() {
          restart()
        },
      },
      {
        key: 'c',
        description: 'clear console',
        action() {
          console.clear()
        },
      },
      {
        key: 'q',
        description: 'quit',
        action() {
          process.exit(0)
        },
      },
    ]

    if (input === 'h') {
      const loggedKeys = new Set<string>()
      globalLogger.info('  Shortcuts')

      for (const shortcut of SHORTCUTS) {
        if (loggedKeys.has(shortcut.key)) continue
        loggedKeys.add(shortcut.key)

        if (shortcut.action == null) continue

        globalLogger.info(
          dim`  press ` +
            bold`${shortcut.key} + enter` +
            dim` to ${shortcut.description}`,
        )
      }

      return
    }

    const shortcut = SHORTCUTS.find((shortcut) => shortcut.key === input)
    if (!shortcut) return

    actionRunning = true
    await shortcut.action()
    actionRunning = false
  }

  const rl = readline.createInterface({
    input: process.stdin,
  })
  rl.on('line', onInput)
  return () => rl.close()
}

export function toArray<T>(
  val: T | T[] | null | undefined,
  defaultValue?: T,
): T[] {
  if (Array.isArray(val)) {
    return val
  } else if (val == null) {
    if (defaultValue) return [defaultValue]
    return []
  } else {
    return [val]
  }
}

export function resolveComma<T extends string>(arr: T[]): T[] {
  return arr.flatMap((format) => format.split(',') as T[])
}

export function resolveRegex<T>(str: T): T | RegExp {
  if (
    typeof str === 'string' &&
    str.length > 2 &&
    str[0] === '/' &&
    str.at(-1) === '/'
  ) {
    return new RegExp(str.slice(1, -1))
  }
  return str
}

export function debounce<T extends (...args: any[]) => any>(
  fn: T,
  wait: number,
): T {
  let timeout: ReturnType<typeof setTimeout> | undefined
  return function (this: any, ...args: any[]) {
    if (timeout) clearTimeout(timeout)
    timeout = setTimeout(() => {
      timeout = undefined
      fn.apply(this, args)
    }, wait)
  } as T
}

export function slash(string: string): string {
  return string.replaceAll('\\', '/')
}

export const noop = <T>(v: T): T => v

export function matchPattern(
  id: string,
  patterns: (string | RegExp)[],
): boolean {
  return patterns.some((pattern) => {
    if (pattern instanceof RegExp) {
      pattern.lastIndex = 0
      return pattern.test(id)
    }
    return id === pattern
  })
}

