import { existsSync } from 'node:fs'
import { readFile, unlink, writeFile } from 'node:fs/promises'
import process from 'node:process'
import { createInterface } from 'node:readline/promises'
import { bold, green, underline } from 'ansis'
import pkg from '../package.json' with { type: 'json' }
import { globalLogger } from './utils/logger.ts'

export async function migrate({
  cwd,
  dryRun,
}: {
  cwd?: string
  dryRun?: boolean
}): Promise<void> {
  if (dryRun) {
    globalLogger.info('Dry run enabled. No changes were made.')
  } else {
    const rl = createInterface({
      input: process.stdin,
      output: process.stdout,
    })

    globalLogger.warn(
      `\n\n` +
        `Before proceeding, review the migration guide at ${underline`https://tsdown.dev/guide/migrate-from-tsup`}, as this process will modify your files.\n` +
        `Uncommitted changes will be lost. Use the ${green`--dry-run`} flag to preview changes without applying them.`,
    )
    const input = await rl.question(bold`Continue? (Y/n) `)
    rl.close()

    const confirm = input.toLowerCase() === 'y' || input === ''
    if (!confirm) {
      globalLogger.error('Migration cancelled.')
      process.exitCode = 1
      return
    }
  }

  if (cwd) process.chdir(cwd)

  let migrated = await migratePackageJson(dryRun)
  if (await migrateTsupConfig(dryRun)) {
    migrated = true
  }
  if (migrated) {
    globalLogger.success(
      'Migration completed. Remember to run install command with your package manager.',
    )
  } else {
    globalLogger.error('No migration performed.')
    process.exitCode = 1
  }
}

const DEP_FIELDS = {
  dependencies: `^${pkg.version}`,
  devDependencies: `^${pkg.version}`,
  peerDependencies: '*',
} as const
async function migratePackageJson(dryRun?: boolean): Promise<boolean> {
  if (!existsSync('package.json')) {
    globalLogger.error('No package.json found')
    return false
  }

  const pkgRaw = await readFile('package.json', 'utf8')
  let pkg = JSON.parse(pkgRaw)
  let found = false

  for (const [field, semver] of Object.entries(DEP_FIELDS)) {
    if (pkg[field]?.tsup) {
      globalLogger.info(`Migrating \`${field}\` to tsdown.`)
      found = true
      pkg[field] = renameKey(pkg[field], 'tsup', 'tsdown', semver)
    }
  }

  if (pkg.scripts) {
    for (const key of Object.keys(pkg.scripts)) {
      if (pkg.scripts[key].includes('tsup')) {
        globalLogger.info(`Migrating \`${key}\` script to tsdown`)
        found = true
        pkg.scripts[key] = pkg.scripts[key].replaceAll(
          /tsup(?:-node)?/g,
          'tsdown',
        )
      }
    }
  }
  if (pkg.tsup) {
    globalLogger.info('Migrating `tsup` field in package.json to `tsdown`.')
    found = true
    pkg = renameKey(pkg, 'tsup', 'tsdown')
  }

  if (!found) {
    globalLogger.warn('No tsup-related fields found in package.json')
    return false
  }

  const pkgStr = `${JSON.stringify(pkg, null, pkgRaw.includes('\t') ? '\t' : 2)}\n`
  if (dryRun) {
    const { createPatch } = await import('diff')
    globalLogger.info('[dry-run] package.json:')
    console.info(createPatch('package.json', pkgRaw, pkgStr))
  } else {
    await writeFile('package.json', pkgStr)
    globalLogger.success('Migrated `package.json`')
  }
  return true
}

const TSUP_FILES = [
  'tsup.config.ts',
  'tsup.config.cts',
  'tsup.config.mts',
  'tsup.config.js',
  'tsup.config.cjs',
  'tsup.config.mjs',
  'tsup.config.json',
]
async function migrateTsupConfig(dryRun?: boolean): Promise<boolean> {
  let found = false

  for (const file of TSUP_FILES) {
    if (!existsSync(file)) continue
    globalLogger.info(`Found \`${file}\``)
    found = true

    const tsupConfigRaw = await readFile(file, 'utf8')
    const tsupConfig = tsupConfigRaw
      .replaceAll(/\btsup\b/g, 'tsdown')
      .replaceAll(/\bTSUP\b/g, 'TSDOWN')

    const renamed = file.replaceAll('tsup', 'tsdown')
    if (dryRun) {
      const { createTwoFilesPatch } = await import('diff')
      globalLogger.info(`[dry-run] ${file} -> ${renamed}:`)
      console.info(
        createTwoFilesPatch(file, renamed, tsupConfigRaw, tsupConfig),
      )
    } else {
      await writeFile(renamed, tsupConfig, 'utf8')
      await unlink(file)
      globalLogger.success(`Migrated \`${file}\` to \`${renamed}\``)
    }
  }

  if (!found) {
    globalLogger.warn('No tsup config found')
  }

  return found
}

// rename key but keep order
function renameKey(
  obj: Record<string, any>,
  oldKey: string,
  newKey: string,
  newValue?: any,
) {
  const newObj: Record<string, any> = {}
  for (const key of Object.keys(obj)) {
    if (key === oldKey) {
      newObj[newKey] = newValue || obj[oldKey]
    } else {
      newObj[key] = obj[key]
    }
  }
  return newObj
}

import { mkdtemp, writeFile } from 'node:fs/promises'
import { tmpdir } from 'node:os'
import { join } from 'node:path'
import util, { type InspectOptionsStylized } from 'node:util'
import Debug from 'debug'
import {
  VERSION as rolldownVersion,
  type BuildOptions,
  type InputOptions,
  type OutputOptions,
  type RolldownPluginOption,
} from 'rolldown'
import { importGlobPlugin } from 'rolldown/experimental'
import pkg from '../../package.json' with { type: 'json' }
import {
  mergeUserOptions,
  type DtsOptions,
  type NormalizedFormat,
  type ResolvedConfig,
} from '../config/index.ts'
import { lowestCommonAncestor } from '../utils/fs.ts'
import { LogLevels } from '../utils/logger.ts'
import { ExternalPlugin } from './external.ts'
import { LightningCSSPlugin } from './lightningcss.ts'
import { NodeProtocolPlugin } from './node-protocol.ts'
import { resolveChunkAddon, resolveChunkFilename } from './output.ts'
import { ReportPlugin } from './report.ts'
import { ShebangPlugin } from './shebang.ts'
import { getShimsInject } from './shims.ts'

const debug = Debug('tsdown:rolldown')

export async function getBuildOptions(
  config: ResolvedConfig,
  format: NormalizedFormat,
  isMultiFormat?: boolean,
  cjsDts: boolean = false,
): Promise<BuildOptions> {
  const inputOptions = await resolveInputOptions(
    config,
    format,
    cjsDts,
    isMultiFormat,
  )

  const outputOptions: OutputOptions = await resolveOutputOptions(
    inputOptions,
    config,
    format,
    cjsDts,
  )

  const rolldownConfig: BuildOptions = {
    ...inputOptions,
    output: outputOptions,
  }
  debug(
    'rolldown config with format "%s" %O',
    cjsDts ? 'cjs dts' : format,
    rolldownConfig,
  )

  return rolldownConfig
}

export async function resolveInputOptions(
  config: ResolvedConfig,
  format: NormalizedFormat,
  cjsDts: boolean,
  isMultiFormat?: boolean,
): Promise<InputOptions> {
  const {
    entry,
    external,
    plugins: userPlugins,
    platform,
    alias,
    treeshake,
    dts,
    unused,
    target,
    shims,
    tsconfig,
    cwd,
    report,
    env,
    nodeProtocol,
    loader,
    name,
    logger,
    cjsDefault,
    banner,
    footer,
    globImport,
  } = config

  const plugins: RolldownPluginOption = []

  if (nodeProtocol) {
    plugins.push(NodeProtocolPlugin(nodeProtocol))
  }

  if (config.pkg || config.skipNodeModulesBundle) {
    plugins.push(ExternalPlugin(config))
  }

  if (dts) {
    const { dts: dtsPlugin } = await import('rolldown-plugin-dts')
    const options: DtsOptions = {
      tsconfig,
      banner: resolveChunkAddon(banner, format, true),
      footer: resolveChunkAddon(footer, format, true),
      ...dts,
    }

    if (format === 'es') {
      plugins.push(dtsPlugin(options))
    } else if (cjsDts) {
      plugins.push(
        dtsPlugin({
          ...options,
          emitDtsOnly: true,
          cjsDefault,
        }),
      )
    }
  }
  if (!cjsDts) {
    if (unused) {
      const { Unused } = await import('unplugin-unused')
      plugins.push(Unused.rolldown(unused === true ? {} : unused))
    }
    if (target) {
      plugins.push(
        // Use Lightning CSS to handle CSS input. This is a temporary solution
        // until Rolldown supports CSS syntax lowering natively.
        await LightningCSSPlugin({ target }),
      )
    }
    plugins.push(ShebangPlugin(logger, cwd, name, isMultiFormat))
    if (globImport) {
      plugins.push(importGlobPlugin())
    }
  }

  if (report && LogLevels[logger.level] >= 3 /* info */) {
    plugins.push(ReportPlugin(report, logger, cwd, cjsDts, name, isMultiFormat))
  }

  if (!cjsDts) {
    plugins.push(userPlugins)
  }

  const define = {
    ...config.define,
    ...Object.keys(env).reduce((acc, key) => {
      const value = JSON.stringify(env[key])
      acc[`process.env.${key}`] = value
      acc[`import.meta.env.${key}`] = value
      return acc
    }, Object.create(null)),
  }
  const inject = shims && !cjsDts ? getShimsInject(format, platform) : undefined

  const inputOptions = await mergeUserOptions(
    {
      input: entry,
      cwd,
      external,
      resolve: {
        alias,
      },
      tsconfig: tsconfig || undefined,
      treeshake,
      platform: cjsDts || format === 'cjs' ? 'node' : platform,
      transform: {
        target,
        define,
        inject,
      },
      plugins,
      moduleTypes: loader,
      logLevel: logger.level === 'error' ? 'silent' : logger.level,
      onLog: cjsDefault
        ? (level, log, defaultHandler) => {
            // suppress mixed export warnings if cjsDefault is enabled
            if (log.code === 'MIXED_EXPORT') return
            defaultHandler(level, log)
          }
        : undefined,
    },
    config.inputOptions,
    [format, { cjsDts }],
  )

  return inputOptions
}

export async function resolveOutputOptions(
  inputOptions: InputOptions,
  config: ResolvedConfig,
  format: NormalizedFormat,
  cjsDts: boolean,
): Promise<OutputOptions> {
  const {
    entry,
    outDir,
    sourcemap,
    minify,
    unbundle,
    banner,
    footer,
    cjsDefault,
  } = config

  const [entryFileNames, chunkFileNames] = resolveChunkFilename(
    config,
    inputOptions,
    format,
  )
  const outputOptions: OutputOptions = await mergeUserOptions(
    {
      format: cjsDts ? 'es' : format,
      name: config.globalName,
      sourcemap,
      dir: outDir,
      exports: cjsDefault ? 'auto' : 'named',
      minify: !cjsDts && minify,
      entryFileNames,
      chunkFileNames,
      preserveModules: unbundle,
      preserveModulesRoot: unbundle
        ? lowestCommonAncestor(...Object.values(entry))
        : undefined,
      banner: resolveChunkAddon(banner, format),
      footer: resolveChunkAddon(footer, format),
    },
    config.outputOptions,
    [format, { cjsDts }],
  )
  return outputOptions
}

export async function getDebugRolldownDir(): Promise<string | undefined> {
  if (!debug.enabled) return
  return await mkdtemp(join(tmpdir(), 'tsdown-config-'))
}

export async function debugBuildOptions(
  dir: string,
  name: string | undefined,
  format: NormalizedFormat,
  buildOptions: BuildOptions,
): Promise<void> {
  const outFile = join(dir, `tsdown.config.${format}.js`)

  handlePluginInspect(buildOptions.plugins)
  const serialized = util.formatWithOptions(
    {
      depth: null,
      maxArrayLength: null,
      maxStringLength: null,
    },
    buildOptions,
  )
  const code = `/*
Auto-generated rolldown config for tsdown debug purposes
tsdown v${pkg.version}, rolldown v${rolldownVersion}
Generated on ${new Date().toISOString()}
Package name: ${name || 'not specified'}
*/

export default ${serialized}\n`
  await writeFile(outFile, code)
  debug(
    'Wrote debug rolldown config for "%s" (%s) -> %s',
    name || 'default name',
    format,
    outFile,
  )
}

function handlePluginInspect(plugins: RolldownPluginOption) {
  if (Array.isArray(plugins)) {
    for (const plugin of plugins) {
      handlePluginInspect(plugin)
    }
  } else if (
    typeof plugins === 'object' &&
    plugins !== null &&
    'name' in plugins
  ) {
    ;(plugins as any)[util.inspect.custom] = function (
      depth: number,
      options: InspectOptionsStylized,
      inspect: typeof util.inspect,
    ) {
      if ('_options' in plugins) {
        return inspect(
          { name: plugins.name, options: (plugins as any)._options },
          options,
        )
      } else {
        return `"rolldown plugin: ${plugins.name}"`
      }
    }
  }
}

