  const pkgStr = `${JSON.stringify(pkg, null, pkgRaw.includes('\t') ? '\t' : 2)}\n`
  if (dryRun) {
    const { createPatch } = await import('diff')
    globalLogger.info('[dry-run] package.json:')
    console.info(createPatch('package.json', pkgRaw, pkgStr))
  } else {
    await writeFile('package.json', pkgStr)
    globalLogger.success('Migrated `package.json`')
  }
  return true
}

const TSUP_FILES = [
  'tsup.config.ts',
  'tsup.config.cts',
  'tsup.config.mts',
  'tsup.config.js',
  'tsup.config.cjs',
  'tsup.config.mjs',
  'tsup.config.json',
]
async function migrateTsupConfig(dryRun?: boolean): Promise<boolean> {
  let found = false

  for (const file of TSUP_FILES) {
    if (!existsSync(file)) continue
    globalLogger.info(`Found \`${file}\``)
    found = true

    const tsupConfigRaw = await readFile(file, 'utf8')
    const tsupConfig = tsupConfigRaw
      .replaceAll(/\btsup\b/g, 'tsdown')
      .replaceAll(/\bTSUP\b/g, 'TSDOWN')

    const renamed = file.replaceAll('tsup', 'tsdown')
    if (dryRun) {
      const { createTwoFilesPatch } = await import('diff')
      globalLogger.info(`[dry-run] ${file} -> ${renamed}:`)
      console.info(
        createTwoFilesPatch(file, renamed, tsupConfigRaw, tsupConfig),
      )
    } else {
      await writeFile(renamed, tsupConfig, 'utf8')
      await unlink(file)
      globalLogger.success(`Migrated \`${file}\` to \`${renamed}\``)
    }
  }

  if (!found) {
    globalLogger.warn('No tsup config found')
  }

  return found
}

// rename key but keep order
function renameKey(
  obj: Record<string, any>,
  oldKey: string,
  newKey: string,
  newValue?: any,
) {
  const newObj: Record<string, any> = {}
  for (const key of Object.keys(obj)) {
    if (key === oldKey) {
      newObj[newKey] = newValue || obj[oldKey]
    } else {
      newObj[key] = obj[key]
    }
  }
  return newObj
}

import path from 'node:path'
import process from 'node:process'
import { dim } from 'ansis'
import Debug from 'debug'
import { prettyName } from '../utils/logger.ts'
import type { ResolvedConfig } from '../config/index.ts'

const debug = Debug('tsdown:publint')

export async function publint(options: ResolvedConfig): Promise<void> {
  if (!options.publint) return
  if (!options.pkg) {
    options.logger.warn(
      prettyName(options.name),
      'publint is enabled but package.json is not found',
    )
    return
  }

  const t = performance.now()
  debug('Running publint')
  const { publint } = await import('publint')
  const { formatMessage } = await import('publint/utils')
  const { messages } = await publint({
    ...(options.publint === true ? {} : options.publint),
    pkgDir: path.dirname(options.pkg.packageJsonPath),
  })
  debug('Found %d issues', messages.length)

  if (!messages.length) {
    options.logger.success(
      prettyName(options.name),
      `No publint issues found`,
      dim`(${Math.round(performance.now() - t)}ms)`,
    )
  }
  let hasError = false
  for (const message of messages) {
    hasError ||= message.type === 'error'
    const formattedMessage = formatMessage(message, options.pkg)
    const logType = (
      { error: 'error', warning: 'warn', suggestion: 'info' } as const
    )[message.type]
    options.logger[logType](prettyName(options.name), formattedMessage)
  }
  if (hasError) {
    debug('Found errors, setting exit code to 1')
    process.exitCode = 1
  }
}

