import path from 'node:path'
import { blue } from 'ansis'
import { up as findUp } from 'empathic/find'
import { fsStat } from '../utils/fs.ts'
import { generateColor, prettyName, type Logger } from '../utils/logger.ts'
import type { UserConfig } from '../config/index.ts'

export function findTsconfig(
  cwd?: string,
  name: string = 'tsconfig.json',
): string | false {
  return findUp(name, { cwd }) || false
}

export async function resolveTsconfig(
  logger: Logger,
  tsconfig: UserConfig['tsconfig'],
  cwd: string,
  name?: string,
): Promise<string | false> {
  const original = tsconfig

  if (tsconfig !== false) {
    if (tsconfig === true || tsconfig == null) {
      tsconfig = findTsconfig(cwd)
      if (original && !tsconfig) {
        logger.warn(`No tsconfig found in ${blue(cwd)}`)
      }
    } else {
      const tsconfigPath = path.resolve(cwd, tsconfig)
      const stat = await fsStat(tsconfigPath)
      if (stat?.isFile()) {
        tsconfig = tsconfigPath
      } else if (stat?.isDirectory()) {
        tsconfig = findTsconfig(tsconfigPath)
        if (!tsconfig) {
          logger.warn(`No tsconfig found in ${blue(tsconfigPath)}`)
        }
      } else {
        tsconfig = findTsconfig(cwd, tsconfig)
        if (!tsconfig) {
          logger.warn(`tsconfig ${blue(original)} doesn't exist`)
        }
      }
    }

    if (tsconfig) {
      logger.info(
        prettyName(name),
        `tsconfig: ${generateColor(name)(path.relative(cwd, tsconfig))}`,
      )
    }
  }

  return tsconfig
}

import path from 'node:path'
import { glob } from 'tinyglobby'
import { fsExists, lowestCommonAncestor } from '../utils/fs.ts'
import { generateColor, prettyName, type Logger } from '../utils/logger.ts'
import type { UserConfig } from '../config/index.ts'

export async function resolveEntry(
  logger: Logger,
  entry: UserConfig['entry'],
  cwd: string,
  name?: string,
): Promise<Record<string, string>> {
  const nameLabel = name ? `[${name}] ` : ''
  if (!entry || Object.keys(entry).length === 0) {
    const defaultEntry = path.resolve(cwd, 'src/index.ts')

    if (await fsExists(defaultEntry)) {
      entry = { index: defaultEntry }
    } else {
      throw new Error(
        `${nameLabel}No input files, try "tsdown <your-file>" or create src/index.ts`,
      )
    }
  }

  const entryMap = await toObjectEntry(entry, cwd)
  const entries = Object.values(entryMap)
  if (entries.length === 0) {
    throw new Error(`${nameLabel}Cannot find entry: ${JSON.stringify(entry)}`)
  }
  logger.info(
    prettyName(name),
    `entry: ${generateColor(name)(entries.map((entry) => path.relative(cwd, entry)).join(', '))}`,
  )
  return entryMap
}

export async function toObjectEntry(
  entry: string | string[] | Record<string, string>,
  cwd: string,
): Promise<Record<string, string>> {
  if (typeof entry === 'string') {
    entry = [entry]
  }
  if (!Array.isArray(entry)) {
    return entry
  }

  const resolvedEntry = (
    await glob(entry, { cwd, expandDirectories: false })
  ).map((file) => path.resolve(cwd, file))
  const base = lowestCommonAncestor(...resolvedEntry)
  return Object.fromEntries(
    resolvedEntry.map((file) => {
      const relative = path.relative(base, file)
      return [
        relative.slice(0, relative.length - path.extname(relative).length),
        file,
      ]
    }),
  )
}

